        case 17: // Recognized '^%{Ident}',	Shortest string "^%G"
{
                                  Token kWord = GetKeyword(yytext); 
                                  if (kWord == Token.kwOutput || kWord == Token.kwImportTokens)
                                      BEGIN(GetPath);
                                  return (int)kWord;
                              }
