        case 40: // In <TheEpilog> Recognized '{CmntStrt}',	Shortest string "/*"
yy_push_state(CodeBlock); 
                              yy_push_state(Comment); 
                              comStart = TokenSpan(); 
                              return (int)Token.codeStart;
